<?php	/*	* Takes care of all the sign-up checkboxes	*	*/	class Yikes_Easy_MC_EDD_Checkbox_Class()	{			public function __construct() {			add_action( 'edd_purchase_form_user_info', array( $this, 'output_checkbox' ) );			add_action( 'edd_payment_meta', array( $this, 'save_checkbox_value' ) );			add_action( 'edd_complete_purchase', array( $this, 'subscribe_from_edd'), 50 );		}				/**		 * Should the checkbox be pre-checked?		 *		 * @return bool		 */		public function is_prechecked() {			$opts = $this->get_options();			return (bool) $opts['precheck'];		}		}	new Yikes_Easy_MC_EDD_Checkbox_Class();		?>