<?php	/*	* Takes care of all the sign-up checkboxes	*	*/	class Yikes_Easy_MC_Comment_Checkbox_Class()	{			public function __construct() {				// hooks for outputting the checkbox			add_action( 'thesis_hook_after_comment_box', array( $this, 'output_checkbox' ), 10 );			add_action( 'comment_form', array( $this, 'output_checkbox' ), 10 );			// hooks for checking if we should subscribe the commenter			add_action( 'comment_post', array( $this, 'subscribe_from_comment' ), 40, 2 );		}				/**		* Outputs a checkbox		*/		public function output_checkbox() {			echo $this->get_checkbox();		}		/**		* @param mixed $args Array or string		* @return string		*/		public function get_checkbox( $args = array() ) {			$checked = ( $this->is_prechecked() ) ? 'checked ' : '';			// set label text			if ( isset( $args['labels'][0] ) ) {				// cf 7 shortcode				$label = $args['labels'][0];			} else {				// $label = $this->get_label_text();			}			// CF7 checkbox?			if( is_array( $args ) && isset( $args['type'] ) ) {				// check for default:0 or default:1 to set the checked attribute				if( in_array( 'default:1', $args['options'] ) ) {					$checked = 'checked';				} else if( in_array( 'default:0', $args['options'] ) ) {					$checked = '';				}			}			// before checkbox HTML (comment, ...)			$before = '<!-- MailChimp for WordPress v'. MC4WP_LITE_VERSION .' - https://mc4wp.com/ -->';			$before .= apply_filters( 'mc4wp_before_checkbox', '', $this->type );			// checkbox			$content = '<p id="mc4wp-checkbox" class="mc4wp-checkbox-' . $this->type .'">';			$content .= '<label>';			$content .= '<input type="checkbox" name="'. esc_attr( $this->checkbox_name ) .'" value="1" '. $checked . ' /> ';			$content .= $label;			$content .= '</label>';			$content .= '</p>';			// after checkbox HTML (..., honeypot, closing comment)			$after = apply_filters( 'mc4wp_after_checkbox', '', $this->type );			$after .= '<textarea name="_mc4wp_required_but_not_really" style="display: none !important;"></textarea>';			$after .= '<!-- / MailChimp for WordPress -->';			return $before . $content . $after;		}						/**		 * Should the checkbox be pre-checked?		 *		 * @return bool		 */		public function is_prechecked() {			return true;		}		}	new Yikes_Easy_MC_Comment_Checkbox_Class();?>