<!--	MailChimp API Debug Settings / Debug Log Table	- display installation stuff here	- php version, wp version, plugin version and debug log--><h3><span><?php _e( 'Debug Settings' , $this->text_domain ); ?></span></h3><div class="inside">		<!-- Settings Form -->	<form action='options.php' method='post'>			<?php settings_fields( 'yikes_inc_easy_mc_debug_settings_page' ); ?>			<label for="yikes-mailchimp-debug-status" style="display:inline-block;margin-bottom:1em;line-height:2.25;"><strong><?php _e( 'Enable Debugging' , $this->text_domain ); ?></strong>			<input type="checkbox" name="yikes-mailchimp-debug-status" id="yikes-mailchimp-debug-status" value="1" style="float:left;margin-top:.5em;" <?php checked( get_option( 'yikes-mailchimp-debug-status' , '' ) , '1' ); ?>>		</label>		<p class="description"><?php _e( "If you encounter an issue with Easy MailChimp you can toggle on debugging to display advanced error messages and start logging errors." , $this->text_domain ); ?></p>				<?php submit_button(); ?>										</form>		<!-- debug log -->	<section id="yikes-easy-mailchimp-debug-log">			<?php			$url = esc_url( 				add_query_arg(					array(						'action' => 'yikes-easy-mc-clear-error-log',						'nonce' => wp_create_nonce( 'clear-yikes-mailchimp-error-log' )					)				)			);		?>		<h2><?php _e( "Debug Log" , $this->text_domain ); ?> &nbsp; <a href="<?php echo $url; ?>" class="button-secondary">Clear Log</a></h2>				<table class="widefat">			<!-- table header -->			<tr>				<th class="row-title"><strong><?php esc_attr_e( 'Error Message', $this->text_domain ); ?></strong></th>				<th><strong><?php esc_attr_e( 'Error Details', $this->text_domain ); ?></strong></th>			</tr>						<?php 				/* Generate oure error logging table */				require_once YIKES_MC_PATH . '/includes/error_log/class-yikes-inc-easy-mailchimp-error-logging.php';				$error_logging = new Yikes_Inc_Easy_Mailchimp_Error_Logging(); 				$error_logging->yikes_easy_mailchimp_generate_error_log_table(); 			?>						<!-- end table body -->					</table>		<!-- end debug table -->			</section>	</div> <!-- .inside -->