<?php/* The template file for displaying our stats in the Admin dashboard widget */?><section id="yikes-easy-mc-widget-stat-holder">	<h3><?php echo $list_data['data'][0]['name']; ?> <small><a href="<?php echo admin_url( 'admin.php?page=yikes-mailchimp-view-list&list-id=' . $list_data['data'][0]['id'] . '' ); ?>" title="<?php _e( 'view List' , $this->text_domain ); ?>"><?php _e( 'view list' , $this->text_domain ); ?></a></small></h3>		<table class="yikes-easy-mc-stats-table">		<thead style="display: none;">			<tr>				<th><?php _e( 'Subscribers' , $this->text_domain ); ?></th>				<th><?php _e( 'Unsubscribed' , $this->text_domain ); ?></th>				<th><?php _e( 'New Since Send' , $this->text_domain ); ?></th>					<th><?php _e( 'Avg. Sub. Rate' , $this->text_domain ); ?></th>			</tr>		</thead>		<tbody>			<tr class="yikes-easy-mc-table-stats-tr yikes-easy-mc-table-stats-tr-first">				<td title="<?php _e( 'Number of active subscribers.' , $this->text_domain ); ?>">					<p class="yikes-easy-mc-dashboard-stat"><?php echo $list_data['data'][0]['stats']['member_count']; ?></p>						<p class="yikes-easy-mc-stat-list-label"><?php _e( 'subscribers' , $this->text_domain ); ?></p>				</td>				<td title="<?php _e( 'Number of users who have unsusbscribed.' , $this->text_domain ); ?>">					<p class="yikes-easy-mc-dashboard-stat"><?php echo $list_data['data'][0]['stats']['unsubscribe_count']; ?></p>						<p class="yikes-easy-mc-stat-list-label"><?php _e( 'unsubscribed' , $this->text_domain ); ?></p>				</td>			</tr>			<tr class="yikes-easy-mc-table-stats-tr  yikes-easy-mc-table-stats-tr-second">				<td title="<?php _e( 'Number of new subscribers since the last campaign was sent.' , $this->text_domain ); ?>">					<p class="yikes-easy-mc-dashboard-stat"><?php echo $list_data['data'][0]['stats']['member_count_since_send']; ?></p>						<p class="yikes-easy-mc-stat-list-label"><?php _e( 'new since send' , $this->text_domain ); ?></p>				</td>				<td title="<?php _e( 'Average number of subscribers per month.' , $this->text_domain ); ?>">					<p class="yikes-easy-mc-dashboard-stat"><?php echo $list_data['data'][0]['stats']['avg_sub_rate']; ?></p>						<p class="yikes-easy-mc-stat-list-label"><?php _e( 'avg. sub. rate' , $this->text_domain ); ?></p>				</td>			</tr>		</tbody>	</table></section>