<div class="wrap">	<?php				// if an error was returned in the most recent query		if( isset( $_GET['sql_error'] ) ) {			// if error logging is turned on, lets display a better error to help narrow things down			// lets also log things to the error log			if( get_option( 'yikes-mailchimp-debug-status' , '' ) == '1' ) {				wp_die( '<strong>' . __( 'Error Creating Form' , $this->text_domain ) . '</strong> <p>' . stripslashes( urldecode( $_GET['sql_error'] ) ) . '</p>' , __( 'Error Creating Form' , $this->text_domain ) );			} else {				wp_die( '<strong>' . __( 'Error Creating Form' , $this->text_domain ) . '</strong><p>' . __( "Please try again. If the error persists please get in contact with the Yikes Inc. support team." , $this->text_domain ) . '</p>' );						}		}				/* Get The Form ID we need to edit */		if( isset( $_GET['id'] ) ) {			global $wpdb;			// grab and store the form ID			$form_id = $_GET['id'];			// return it as an array, so we can work with it to build our form below			$form_results = $wpdb->get_results( 'SELECT * FROM ' . $wpdb->prefix . 'yikes_easy_mc_forms WHERE id = ' . $form_id . '', ARRAY_A );			// Get all results for our form switcher			$all_forms = $wpdb->get_results( 'SELECT * FROM ' . $wpdb->prefix . 'yikes_easy_mc_forms', ARRAY_A );									// if the form was not found return an error			if( !$form_results ) {				wp_die( __( "Whoops! It looks like this form doesnt exist. If this error persists you may want to toggle on debugging on the" , $this->text_domain ) . ' <a href="' . admin_url( 'admin.php?page=yikes-inc-easy-mailchimp-settings&section=debug-settings' ) . '">' . __( 'debug settings page' , $this->text_domain ) . '</a>.' , __( 'Error' , $this->text_domain ) );			}						// store our results			$form = $form_results[0];			$custom_styles = json_decode( $form['custom_styles'] , true );			$optin_settings = json_decode( $form['optin_settings'] , true );			$submission_settings = json_decode( $form['submission_settings'] , true );			$error_messages = json_decode( $form['error_messages'] , true );			$custom_notifications = json_decode( $form['custom_notifications'] , true );						// Check for a transient, if not - set one up for one hour			if ( false === ( $list_data = get_transient( 'yikes-easy-mailchimp-list-data' ) ) ) {				// initialize MailChimp Class				$MailChimp = new MailChimp( get_option( 'yikes-mc-api-key' , '' ) );				// retreive our list data				$list_data = $MailChimp->call( 'lists/list' , array( 'apikey' => get_option( 'yikes-mc-api-key' , '' ) ) );				// set our transient				set_transient( 'yikes-easy-mailchimp-list-data', $list_data, 1 * HOUR_IN_SECONDS );			}						// get the list data			try {				$api_key = get_option( 'yikes-mc-api-key' , '' );				$MailChimp = new MailChimp( $api_key );				// retreive our list data				$available_merge_variables = $MailChimp->call( 'lists/merge-vars' , array( 'apikey' => $api_key , 'id' => array( $form['list_id'] ) ) );			} catch ( Exception $e ) {				$merge_variable_error = '<p class="description" style="padding-top:.5em;">' . __( 'Error' , $this->text_domain ) . ' : ' . $e->getMessage() . '.</p>';				wp_die( __( "Uh Oh...It looks like we ran into an error! Please reload the page and try again. If the error persists, please contact the Yikes Inc. support team.", $this->text_domain ) , 500 );			}						// get the interest group data			try {				$interest_groupings = $MailChimp->call( 'lists/interest-groupings' , array( 'apikey' => $api_key , 'id' => $form['list_id'] ) );			} catch( Exception $error ) {				$no_interest_groupings = '<p class="description" style="padding-top:.5em;">' . $error->getMessage() . '.</p>';			}		} else {			wp_die( __( 'Oh No!' , $this->text_domain ) , __( 'Error' , $this->text_domain ) );		}				/* Confirm we've retreived our form data */		if( empty( $form ) ) { 					wp_die( __( "We've encountered an error. Please try again. If the error persists, please contact support." , $this->text_domain ) , __( 'Error' , $this->text_domain ) );				} else {					/* Build Our Update Form URL */			// create a custom URL to allow for creating fields			$url = esc_url( 				add_query_arg(					array(						'action' => 'yikes-easy-mc-update-form',						'nonce' => wp_create_nonce( 'update-mailchimp-form'.-$form['id'] )					)				)			);			/* Display Our Form */			?>				<!-- Freddie Logo -->				<img src="<?php echo YIKES_MC_URL . 'includes/images/MailChimp_Assets/Freddie_60px.png'; ?>" alt="Freddie - MailChimp Mascot" style="float:left;margin-right:10px;" />									<h2>Easy MailChimp by Yikes Inc. | <?php echo __( 'Edit' , $this->text_domain ) . ' ' . $form['form_name']; ?></h2>										<!-- Settings Page Description -->				<p class="yikes-easy-mc-about-text about-text"><?php _e( 'Update this MailChimp forms settings and front end styles below.' , $this->text_domain ); ?></p>								<?php				if( isset( $_REQUEST['updated-form'] ) && $_REQUEST['updated-form'] == 'true' ) {					?>					<div class="updated manage-form-admin-notice">						<p><?php _e( 'MailChimp Form successfully updated.', $this->text_domain ); ?></p>					</div>					<?php				}				?>								<div id="poststuff">					<!-- BEGIN UPDATE FORM -->					<form id="edit-yikes-mc-form" method="POST" action="<?php echo $url; ?>"> 										<div id="post-body" class="metabox-holder columns-2">											<!-- main content -->						<div id="post-body-content">							<div class="meta-box-sortables ui-sortable">								<div class="postbox yikes-easy-mc-postbox">									<div class="inside">																														<label for="form-name">											<h3 class="bg-transparent"><?php _e( 'Form Name' , $this->text_domain ); ?></h3>											<input autocomplete="disabled" id="form-name" name="form-name" type="text" value="<?php echo stripslashes( esc_html( $form['form_name'] ) ); ?>" class="widefat" />											<p class="description"><?php _e( "The title of this signup form." , $this->text_domain ); ?></p>										</label>																				<label for="form-description">											<h3 class="bg-transparent"><?php _e( 'Form Description' , $this->text_domain ); ?></h3>											<textarea name="form-description" id="form-description" class="large-text" style="width:100%;resize:vertical;min-height:65px;max-height:100px;"><?php echo isset( $form['form_description'] ) ? stripslashes( esc_html( $form['form_description'] ) ) : ''; ?></textarea>											<p class="description"><?php _e( "Descriptions are optional and you may choose to display it to the user on the frontend." , $this->text_domain ); ?></p>										</label>																													</div>									<!-- .inside -->								</div>								<!-- .postbox -->							</div>							<!-- .meta-box-sortables .ui-sortable -->														<!-- TOOLBAR -->								<div id="yikes_easy_mc_toolbar">																	<ul id="yikes_easy_mc_toolbar_links">										<li class="hidden_setting_list">											<a class="hidden_setting form-builder selected_hidden_setting" onclick="return false;" title="<?php __( 'Customize Form Fields' , $this->text_domain ); ?>" href="#" alt="form-builder-label"> <?php _e( 'Form Builder' , $this->text_domain ); ?></a>											<div class="selected_setting_triangle"></div>										</li>										<li class="hidden_setting_list">											<a class="hidden_setting error-messages" onclick="return false;" title="<?php _e( 'Customize Form Messages' , $this->text_domain ); ?>" href="#" alt="form-error-messages"> <?php _e( 'Custom Messages' , $this->text_domain ); ?></a>										</li>										<li class="hidden_setting_list">											<a class="hidden_setting form-customizer" onclick="return false;" title="<?php __( 'Customize Form Appearance (pro only)' , $this->text_domain ); ?>" href="#" alt="form-customizer-label"> <?php _e( 'Form Customizer (pro only)' , $this->text_domain ); ?></a>										</li>										<li class="hidden_setting_list">											<a class="hidden_setting custom-notifications" onclick="return false;" title="<?php _e( 'Custom Notifications (pro only)' , $this->text_domain ); ?>" href="#" alt="form-custom-notifications"> <?php _e( 'Custom Notifications (pro only)' , $this->text_domain ); ?></a>										</li>														</ul>																	</div>							<!-- END TOOLBAR -->														<div class="meta-box-sortables ui-sortable" id="hidden-option-data-container">								<div class="postbox yikes-easy-mc-postbox">																					<div class="inside">																			<!-- Form Builder Label -->																				<label for="form" class="hidden-setting-label" id="form-builder-label">																					<div id="poststuff">												<div id="post-body" class="metabox-holder columns-2">													<!-- main content -->													<div id="post-body-content">														<div class="meta-box-sortables ui-sortable">															<div class="postbox yikes-easy-mc-postbox">																<!-- container title -->																<h3 style="padding-left:12px;"><?php _e( 'Form Builder' , $this->text_domain ); ?></h3>																<p  style="padding:0 12px;margin:1em 0;" class="description"><?php _e( 'Select a form field from the right to add to this form. Once added to the form, you can click it to reveal advanced options for the field, or drag it to re-arrange the position in the form.' , $this->text_domain );?></p>																<div id="form-builder-container" class="inside">																	<!-- #poststuff -->																	<?php echo $this->generate_form_editor( json_decode( $form['fields'] , true ) , $form['list_id'] , $available_merge_variables , isset( $interest_groupings ) ? $interest_groupings : array() ); ?>																</div>																<!-- .inside -->															</div>															<!-- .postbox -->														</div>														<!-- .meta-box-sortables .ui-sortable -->													</div>													<!-- post-body-content -->													<!-- sidebar -->													<div id="postbox-container-1" class="postbox-container">														<div class="meta-box-sortables">															<div class="postbox yikes-easy-mc-postbox" style="overflow:hidden;">																<h3 style="padding-left:12px;"><span><?php _e( "Form Fields &amp; Interest Groups" , $this->text_domain ); ?></span></h3>																<div class="inside">																																		<h4 class="nav-tab-wrapper mv_ig_list">																		<a href="#" alt="merge-variables" class="nav-tab nav-tab-active"><div class="arrow-down"></div><?php _e( 'Form Fields' , $this->text_domain ); ?></a>																		<?php if( !isset( $merge_variable_error ) ) { ?>																			<a href="#" alt="interest-groups" class="nav-tab"><?php _e( 'Interest Groups' , $this->text_domain ); ?></a>																		<?php } else { ?>																			<a href="#" alt="interest-groups" class="nav-tab" disabled="disabled" style="opacity:.35" title="<?php _e( "No Interest Groups Exist" , $this->text_domain ); ?>"><?php _e( 'Interest Groups' , $this->text_domain ); ?></a>																		<?php } ?>																	</h4>																																			<div id="container-container">																																			<div id="merge-variables-container" class="list-container">																			<?php																				if( ! isset( $merge_variable_error ) ) {																					// build a list of available merge variables,																					// but exclude the ones already assigned to the form																					echo '<p class="description" style="padding-top:.5em;">' . __( "Select a field below to add to the form builder." , $this->text_domain ) . '</p>';																					$this->build_available_merge_vars( json_decode( $form['fields'] , true ) , $available_merge_variables );																				} else {																					echo $merge_variable_error;																				}																			?>																		</div>																																				<div id="interest-groups-container" class="list-container">																			<?php																				if( isset( $interest_groupings ) && count( $interest_groupings ) > 1 ) {																					// build a list of available merge variables,																					// but exclude the ones already assigned to the form																					echo '<p class="description" style="padding-top:.5em;">' . __( "Select an interest group below to add to the form builder." , $this->text_domain ) . '</p>';																					// $this->build_available_merge_vars( json_decode( $form['fields'] , true ) , $available_merge_variables );																					$this->build_available_interest_groups( json_decode( $form['fields'] , true ) , $interest_groupings , $form['list_id'] );																				} else {																					echo $no_interest_groupings;																				}																			?>																		</div>																																			</div>																																	</div>																<!-- .inside -->															</div>																														<!-- .postbox -->														</div>														<!-- .meta-box-sortables -->													</div>													<!-- #postbox-container-1 .postbox-container -->												</div>												<!-- #post-body .metabox-holder .columns-2 -->												<br class="clear">											</div>																					</label>										<!-- End Form Builder Label -->																				<!-- Form Customizer -->																				<label for="form" class="hidden-setting-label" id="form-customizer-label" style="display:none;">																					<div id="poststuff">												<div id="post-body" class="metabox-holder columns-2">													<!-- main content -->													<div id="post-body-content">														<div class="meta-box-sortables ui-sortable">															<div class="postbox yikes-easy-mc-postbox">				