<!-- required styles --><style>#postbox-container-1 .postbox { padding-bottom: 1em; }</style><!-- toggle optin message container on change --><script type="text/javascript">function toggleOptinValue( selected ) {	if( selected.value == 'true' ) {		jQuery( '.yks-mailchimp-single-optin-message' ).slideUp( 'fast' , function() {			jQuery( '.yks-mailchimp-double-optin-message' ).slideDown( 'fast' );		});	} else {		jQuery( '.yks-mailchimp-double-optin-message' ).slideUp( 'fast' , function() {			jQuery( '.yks-mailchimp-single-optin-message' ).slideDown( 'fast' );		});	}}</script><?php		/* Get and Store Option Values */	if( get_option( 'yikes-mc-api-validation' , 'invalid_api_key' ) == 'valid_api_key' ) {		$api_connection = '<span id="connection-container" class="api-connected" title="' . __( "Your site is currently connected to the MailChimp API" , "yikes-inc-easy-mailchimp-extender" ) . '"><span class="dashicons dashicons-yes yikes-mc-api-connected"></span> ' . __( "Connected" , 'yikes-inc-easy-mailchimp-extender' ) . '</span>';		$api_error_response = '';		// when the API key is changed, let's clear the transient data (incase users switch MailChimp accounts)		// Delete list data transient		delete_transient( 'yikes-easy-mailchimp-list-data' );		// Delete list account data		delete_transient( 'yikes-easy-mailchimp-account-data' );		// Delete list account data		delete_transient( 'yikes-easy-mailchimp-profile-data' );	} else {		$api_connection = '<span id="connection-container" class="api-not-connected"><span class="dashicons dashicons-no-alt yikes-mc-api-not-connected"></span>  ' . __( "Not Connected" , 'yikes-inc-easy-mailchimp-extender' ) . '</span>';		if( get_option( 'yikes-mc-api-invalid-key-response' , '' ) != '' ) {				$api_error_response = '<p><small><i class="dashicons dashicons-no-alt"></i> ' . get_option( 'yikes-mc-api-invalid-key-response' , '' ) . '</small></p>';		} else {			$api_error_response = '';		}	}?>	<!-- 		Actual Settings Form 		Chyea --	-->	<div class="wrap">			<!-- Freddie Logo -->		<img src="<?php echo YIKES_MC_URL . 'includes/images/MailChimp_Assets/Freddie_60px.png'; ?>" alt="<?php __( 'Freddie - MailChimp Mascot' , 'yikes-inc-easy-mailchimp-extender' ); ?>" style="float:left;margin-right:10px;" />				<h2>Easy MailChimp by Yikes Inc. | <?php if ( isset( $_REQUEST['section'] ) ) { echo ucwords( str_replace( '-', ' ', $_REQUEST['section'] ) ); } else { echo __( 'General Settings' , 'yikes-inc-easy-mailchimp-extender' ); } ?></h2>								<!-- Settings Page Description -->		<p class="yikes-easy-mc-about-text about-text"><?php _e( 'Easy MailChimp Forms allows you to painlessly add MailChimp sign up forms to your WordPress site and track user activity with interactive reports.' , 'yikes-inc-easy-mailchimp-extender' ); ?></p>		<?php			/* Success Messages on Options Updated */			if( isset( $_REQUEST['settings-updated'] ) && $_REQUEST['settings-updated'] == 'true' ) {				?>				<div class="updated manage-form-admin-notice">					<p><?php _e( 'Settings successfully updated.', 'yikes-inc-easy-mailchimp-extender' ); ?></p>				</div>				<?php			}			/* MailChimp API Cleared Successfully message */			if( isset( $_REQUEST['transient-cleared'] ) && $_REQUEST['transient-cleared'] == 'true' ) {				?>				<div class="updated manage-form-admin-notice">					<p><?php _e( 'MailChimp API Cache successfully cleared.', 'yikes-inc-easy-mailchimp-extender' ); ?></p>				</div>				<?php			}			/* Error Log Clear Messages */				/* Success Messages on Options Updated */				if( isset( $_REQUEST['error-log-cleared'] ) && $_REQUEST['error-log-cleared'] == 'true' ) {					?>					<div class="updated manage-form-admin-notice">						<p><?php _e( 'Error log successfully cleared.', 'yikes-inc-easy-mailchimp-extender' ); ?></p>					</div>					<?php				}				/* MailChimp API Cleared Successfully message */				if( isset( $_REQUEST['error-log-cleared'] ) && $_REQUEST['error-log-cleared'] == 'false' ) {					?>					<div class="error manage-form-admin-notice">						<p><?php _e( "Whoops! We've encountered an error while trying to clear the error log. Please refresh the page and try again. If the error persists please get in touch with the Yikes Inc. support team.", 'yikes-inc-easy-mailchimp-extender' ); ?></p>					</div>					<?php				}								/* Display a success message if our error log was sucessfully created, or display an error if not */				if( isset( $_GET['error_log_created'] ) && $_GET['error_log_created'] == 'true' ) {					?>					<div class="updated">						<p><?php _e( 'Error log successfully created. You may now start logging errors.', 'yikes-inc-easy-mailchimp-extender' ); ?></p>					</div>					<?php				} else if( isset( $_GET['error_log_created'] ) && $_GET['error_log_created'] == 'false' ) {					?>					<div class="error">						<p><?php _e( urldecode( $_GET['error_message'] ) , 'yikes-inc-easy-mailchimp-extender' ); ?></p>					</div>					<?php				} 						?>												<!-- entire body content -->		<div id="poststuff">				<div id="post-body" class="metabox-holder columns-2">							<!-- main content -->				<div id="post-body-content">										<div class="meta-box-sortables ui-sortable">												<div class="postbox yikes-easy-mc-postbox">													<?php if( !isset( $_REQUEST['section'] ) || $_REQUEST['section'] == '' ) { 								include YIKES_MC_PATH . 'admin/partials/menu/options-sections/general-settings.php';											} else {								include YIKES_MC_PATH . 'admin/partials/menu/options-sections/' . $_REQUEST['section'] . '.php';									}							?>												</div> <!-- .postbox -->											</div> <!-- .meta-box-sortables .ui-sortable -->									</div> <!-- post-body-content -->								<!-- sidebar -->				<div id="postbox-container-1" class="postbox-container">															<div class="meta-box-sortables">												<div class="postbox yikes-easy-mc-postbox">												