<?php	/*	*	Takes care of all the comment checkboxes	*	below the standard WordPress commenting	*/	class Yikes_Easy_MC_Comment_Checkbox_Class extends Yikes_Easy_MC_Checkbox_Integration_Class {			public function __construct() {				// hooks for outputting the checkbox			add_action( 'thesis_hook_after_comment_box', array( $this, 'output_checkbox' ), 10 );			add_action( 'comment_form_field_comment', array( $this, 'output_checkbox' ), 10 );			// hooks for checking if we should subscribe the commenter			add_action( 'comment_post', array( $this, 'subscribe_from_comment' ), 40, 2 );		}							/**		* Outputs a checkbox		*/		public function output_checkbox( $comment_field ) {			echo $comment_field . $this->yikes_get_checkbox();		}					/**		 *	Hook to submit the data to MailChimp when 		 *	a new comment is submitted		 *		 *	@since 6.0.0		**/		public function subscribe_from_comment( $comment_id , $comment_approvided ) {				// store comment data			$comment_data = get_comment( $comment_id );			// subscribe the user 			try{				$this->subscribe_user_integration( sanitize_email( $comment_data->comment_author_email ) , 'comment_form' );			} catch( Exception $e ) {				return $e->getMessage();			}		}			}	new Yikes_Easy_MC_Comment_Checkbox_Class;?>