<?php	class YIKES_Inc_Easy_MailChimp_Public_Ajax	{			/**		 * Thetext domain of this plugin		 *		 * @since    1.0.0		 * @access   private		 * @var      string    $version    Used for internationalization		 */		private $text_domain = 'yikes-inc-easy-mailchimp-extender';			public function __construct() {							// ajax process form submission			add_action( 'wp_ajax_nopriv_process_form_submission', array( $this , 'process_form_submission' ) , 10 );			add_action( 'wp_ajax_process_form_submission', array( $this , 'process_form_submission' ) , 10 );		}				/*		*	Process form submisssions sent via ajax from the front end		*	$form_data - serialized form data submitted		*/		public function process_form_submission() {			// include our ajax processing file			include_once( YIKES_MC_PATH . 'public/partials/shortcodes/process/process_form_submission_ajax.php' );			exit();			wp_die();		}								} // end class		new YIKES_Inc_Easy_MailChimp_Public_Ajax();	?>